mapper.select:
SELECT favorite_index,
       user_no,
       kind,
       kind_no,
       DATE_FORMAT(regist_date, '%Y-%m-%d %T') regist_date
FROM 	members_favorite
WHERE 	user_no = #{userNo}
        AND kind = #{kind}
        AND kind_no = #{kindNo};

mapper.delete:
DELETE FROM members_favorite
WHERE 	favorite_index = #{favoriteIndex}
        AND user_no = #{userNo};
